/** @format */

import {
  ADDED,
  ADD_COLOR,
  ALL_CLEARED,
  ALL_COMPLETED,
  DELETED,
  TOGGLED,
} from "./actionTypes";

export const added = (todoText) => {
  return {
    type: ADDED,
    payload: todoText,
  };
};
export const addColor = (todoId, color) => {
  return {
    type: ADD_COLOR,
    payload: {
      todoId,
      color,
    },
  };
};
export const toggled = (todoId) => {
  return {
    type: TOGGLED,
    payload: todoId,
  };
};
export const deleted = (todoId) => {
  return {
    type: DELETED,
    payload: todoId,
  };
};
export const allCompleted = () => {
  return {
    type: ALL_COMPLETED,
  };
};
export const allCleared = () => {
  return {
    type: ALL_CLEARED,
  };
};
