/** @format */

import { useSelector, useDispatch } from "react-redux";
import { addColor, deleted, toggled } from "../redux/todos/actions";
import cancelImage from "../assets/images/cancel.png";

export default function Todo() {
  const todos = useSelector((state) => state);
  const dispatch = useDispatch();
  return (
    todos &&
    todos.map((getTodo) => {
      const { id, todo, color, completed } = getTodo;
      return (
        <div className="flex justify-start items-center p-2 hover:bg-gray-100 hover:transition-all space-x-4 border-b border-gray-400/20 last:border-0">
          <div className="rounded-full bg-white border-2 border-gray-400 w-5 h-5 flex flex-shrink-0 justify-center items-center mr-2 border-green-500 focus-within:border-green-500">
            <input
              type="checkbox"
              className="opacity-0 absolute rounded-full"
              onClick={dispatch(toggled(id))}
            />
            <svg
              className={`${
                !completed ? `hidden` : ``
              } fill-current w-3 h-3 text-green-500 pointer-events-none`}
              viewBox="0 0 20 20"
            >
              <path d="M0 11l2-2 5 5L18 3l2 2L7 18z" />
            </svg>
          </div>

          <div
            className={`select-none flex-1 ${completed ? `line-through` : ``}`}
          >
            {todo}
          </div>

          <div
            onClick={() => dispatch(addColor(id, "green"))}
            className={`flex-shrink-0 h-4 w-4 rounded-full border-2 ml-auto cursor-pointer border-${color}-500 hover:bg-${color}-500 `}
          ></div>
          <div
            onClick={() => dispatch(addColor(id, "yellow"))}
            className={`flex-shrink-0 h-4 w-4 rounded-full border-2 ml-auto cursor-pointer border-${color}-500 hover:bg-${color}-500 `}
          ></div>
          <div
            onClick={() => dispatch(addColor(id, "red"))}
            className={`flex-shrink-0 h-4 w-4 rounded-full border-2 ml-auto cursor-pointer border-${color}-500 hover:bg-${color}-500 `}
          ></div>

          <img
            src={cancelImage}
            className="flex-shrink-0 w-4 h-4 ml-2 cursor-pointer"
            alt="Cancel"
            onClick={() => dispatch(deleted(id))}
          />
        </div>
      );
    })
  );
}
